import React, { Component } from 'react';
import GridSizer from '../components/newNons/GridSizer'
import GridContainer from './GridContainer'
import { connect } from 'react-redux'


class NonContainer extends Component {

  nonogramName= (nonogram)=>{
    if (nonogram){ return <h2>{nonogram.name || "Unnamed"}</h2>}
  }


  render() {
    let nonogram= this.props.nonograms[this.props.match.nonID-1]
    return (
      <div>
        {nonogramName(nonogram)}
        <GridContainer />
      </div>
    );
  }
}



export default connect(null)(NonContainer)
